bool isValid(ll k){
	for(ll m = 0; m * m < k; m++){
		ll n = sqrt(k - m * m);
		pair<ll, ll> pp = mp(m, n);
		if(n * n == k - m * m && ss.find(pp) == ss.end() && abs(m * p + n * q) % k == 0 && abs(m * q - n * p) % k == 0){
			//cout << m << " " << n << endl;
			return true;
		}
		pp = mp(m, -n); n = -n;
		if(n * n == k - m * m && ss.find(pp) == ss.end() && abs(m * p + n * q) % k == 0 && abs(m * q - n * p) % k == 0){
			//cout << m << " " << n << endl;
			return true;
		}
		n = -n;
		pp = mp(-m, -n); m = -m; n = -n;
		if(n * n == k - m * m && ss.find(pp) == ss.end() && abs(m * p + n * q) % k == 0 && abs(m * q - n * p) % k == 0){
