typedef pair<int, int> P;
typedef set<P> S;

S bases[N];

void init_bases() {
    for (int n=0; n*n<N; ++n) {
        for (int m=n; true; ++m) {
            int b = n*n+m*m;
            if (b >= N) {
                break;
            }
            if (b > 1) {
                bases[b].insert(P(n, m));
                bases[b].insert(P(m, n));
            }
        }
    }
}

bool is_prime(int p, int q) {
    int b0 = p*p+q*q;
    for (int b1=2; b1<N; ++b1) {
        int b2 = b0/b1;
        if (b0%b1 > 0) {
            continue;
        }
        for (S::iterator iter=bases[b2].begin(); iter!=bases[b2].end(); ++iter) {
            int m = iter->first;
            int n = iter->second;
            if ((m*p+n*q)%b2 == 0 && (m*q-n*p)%b2 == 0) {
                return false;
            }
        }
    }
    return true;
}

int main() {
    int n, p, q;
    init_bases();
    cin >> n;
    for (int i=0; i<n; ++i) {
        cin >> p >> q;
        if (is_prime(p, q)) {
            cout << "P" << endl;
        } else {
            cout << "C" << endl;
        }
    }
    return 0;
}

