                            if (M == -p and N == -q) continue;
                            if (M == q and N == -p) continue;
                            bool valid = 1;
                            if ((M * p + N * q) % i) valid = 0;
                            if ((M * q - N * p) % i) valid = 0;
                            if (valid) {
                                ans = 'C';
                            }
                        }
                    }
                }
            }
        }
        cout << ans << endl;
    }

    return 0;

}

