        const int sqb = b * b;
        const int sqab = sqa + sqb;
        for (int i = 2; i * i <= sqab; i++) {
            if (sqab % i == 0) {
                if (sqa != i || sqb != sqab / i ) {
                    isNotPrime |= !!(squaresum.count(i) && squaresum.count(sqab / i));
                    if (squaresum.count(i) && squaresum.count(sqab / i)) {
                        //cout<<a<<" "<<b<<" "<<i<<" "<<sqab/i<<endl;
                    }
                }
            }
        }
        printf("%c\n", (!isNotPrime ? 'P' : 'C'));
    }
    exit(0);
}

