bool isPrime(int p, int q){

  if(p*p+q*q <= 1) return false;

  for(int n = -MAX; n < MAX; n++)
    for(int m = -MAX; m < MAX; m++){
      if(n*n+m*m <= 1) continue;
      if(abs(n)==abs(p)&&abs(m)==abs(q)||abs(n)==abs(q)&&abs(m)==abs(p)) continue;
      if(__gcd(m*p+n*q,m*q-n*p) == m*m+n*n) return false;
    }
  return true;
}

