map<ll,ll> prime_factor(ll n) {
	map<ll,ll> res;
	for(ll i=2; i*i <= n; i++) {
		while(n%i == 0) {
			res[i]++;
			n /= i;
		}
	}

	if(n != 1) res[n] = 1;
	return res;
}

int main() {
	int n;
	cin >> n;

	rep(i, n) {
		int m, n;
		cin >> m >> n;

		int s = m * m + n * n;
		int cnt = 0;

		REP(i, 1, s + 1) {
			if(s % i == 0) {
				map<ll, ll> ret = prime_factor(s / i);
				map<ll, ll>::iterator ite;
				bool flag = true;
				for(ite = ret.begin(); ite != ret.end(); ite++) {
					if(ite->first  % 4 != 3) continue;
					if(ite->second % 2 == 0) continue;
					flag = false;
				}

				if(flag) {
					cnt++;
				}
			}
		}

		if(cnt >= 3) cout << "C" << endl;
		else cout << "P" << endl;
	}

	return 0;
}

