int u[20000];
void make_table(){
  for(int i=0;i*i<=20000;i++){
    t.push_back(i*i);
  }
  for(int i=0;i<(int)t.size();i++){
    int I=t[i];
    for(int j=i;j<(int)t.size();j++){
      int J=t[j];
      if(I+J>=20000)break;
      u[I+J]++;
    }
  }
}

int main(){
  make_table();
  int Tc;
  cin>>Tc;
  while(Tc--){
    cin>>n>>m;
    int d=n*n+m*m;
    int ans=0,flg=0;
    for(int i=1;i<=d;i++){
      if(d%i!=0)continue;
      ans+=u[i]*u[d/i];
      if(ans>=3){
	flg=1;break;
      }
    }
    if(flg){
      cout<<'C'<<endl;
    }else cout<<'P'<<endl;

  }
  return 0;
}

