const int LIM = 20001;
int sq[LIM];

bool check(int a, int b, int c, int d, int m, int n) {
//    vector<pair<int, int>> ng_pair = {
//            {0,  1},
//            {0,  -1},
//            {1,  0},
//            {-1, 0},
//            {m,  n},
//            {-m, -n},
//            {n,  -m},
//            {-n, m}
//    };

    vector<pair<int, int>> ng_pair;
    ng_pair.emplace_back(0, 1);
    ng_pair.emplace_back(0, -1);
    ng_pair.emplace_back(1, 0);
    ng_pair.emplace_back(-1, 0);
    ng_pair.emplace_back(m, n);
    ng_pair.emplace_back(-m, -n);
    ng_pair.emplace_back(n, -m);
    ng_pair.emplace_back(-n, m);


    for (auto p : ng_pair) {
        if (make_pair(a, b) == p) return false;
        if (make_pair(c, d) == p) return false;
    }

    if ((a * m + b * n) % (a * a + b * b)) return false;
    if ((a * n - b * m) % (a * a + b * b)) return false;

    return true;
}

bool check_abs(int a, int b, int c, int d, int m, int n) {
    for (int A = -1; A <= 1; A += 2) {
        for (int B = -1; B <= 1; B += 2) {
            for (int C = -1; C <= 1; C += 2) {
                for (int D = -1; D <= 1; D += 2) {
                    if (check(a * A, b * B, c * C, d * D, m, n)) {
                        return true;
                    }
                }
            }
        }
    }
    return false;
}

void solve() {
    int m, n;
    cin >> m >> n;

    for (int l = 1; l * l <= n * n + m * m; ++l) {
        if ((n * n + m * m) % (l)) continue;
        int r = (n * n + m * m) / (l);
        if (r == 0) continue;

        for (int a = 1; a * a <= l; ++a) { // <=が怖い
            if (sq[l - a * a] == -1) continue;
            int b = sq[l - a * a];
            for (int c = 1; c * c <= l; ++c) { // <=が怖い
                if (sq[l - c * c] == -1) continue;
                int d = sq[l - c * c];

                if (check_abs(a, b, c, d, m, n)) {
                    cout << "C" << endl;
                    return;
                }
            }
        }
    }
    cout << "P" << endl;
}


