const int maxdep=20000;
int T,n,m,sum;
int a[maxdep+5][2],cnt,ans;
bool f[maxdep+5];
void pre()
{
    memset(f,0,sizeof(f));
    f[0]=1;
    for (int i=1;i*i<maxdep;i++)
        for (int j=1;i*i+j*j<maxdep;j++)
            f[i*i+j*j]=1;
}
void sep()
{
    sum=n*n+m*m;
    cnt=0;
    for (int i=2;i*i<=sum;i++)
        if (sum%i==0)
        {
            a[cnt][0]=i;
            a[cnt][1]=0;
            while (sum%i==0)
            {
                sum/=i;
                a[cnt][1]++;
            }
            cnt++;
        }
    if (sum!=1)
    {
        a[cnt][0]=sum;
        a[cnt][1]=1;
        cnt++;
    }
    sum=n*n+m*m;
//    for (int i=0;i<cnt;i++) printf("%d^%d*",a[i][0],a[i][1]);
//    putchar('\n');
}
void dfs(int dep,int now)
{
    if (dep==cnt)
    {
        if (now!=1&&now!=sum&&f[now]&&f[sum/now]) ans=1;
    }
    else for (int i=0;i<=a[dep][1];i++)
    {
        dfs(dep+1,now);
        now*=a[dep][0];
    }
}
int main()
{
    pre();
    scanf("%d",&T);
    while (T--)
    {
        scanf("%d%d",&n,&m);
        sep();
        ans=0;
        dfs(0,1);
        if (ans) puts("C");
        else puts("P");
    }
    return 0;
}

