using namespace std;

inline bool check(int p, int q, int m, int n) {
	if(m * p + n * q == 0 || m * q - n * p == 0)
		return false;
	const int square_sum = m * m + n * n;
	return (((m * p + n * q) % square_sum) | ((m * q - n * p) % square_sum)) == 0;
}

int main() {
	cin.tie(0);
	ios::sync_with_stdio(false);

	const int MAX = 20000;

	int t;
	cin >> t;

	unordered_set<int> s;
	for(int i = 0; i * i < MAX; ++i) {
		const int rest = MAX - i * i;
		for(int j = (i ? i : 2); j * j < rest; ++j) {
			s.insert(i * i + j * j);
		}
	}

	for(int i = 0; i < t; ++i) {
		int m, n;
		cin >> m >> n;

		const int square_sum_mn = m * m + n * n;
		for(int x = 0; x * x < square_sum_mn; ++x) {
			const int rest = square_sum_mn - x * x;
			for(int y = (x ? x : 2); y * y < rest; ++y) {
				const int square_sum_xy = x * x + y * y;
				if(square_sum_mn % square_sum_xy == 0
				   && s.find(square_sum_mn / square_sum_xy) != s.end()) {
					if(check(m, n, x, y) || check(m, n, -x, y)
					   || check(m, n, x, -y) || check(m, n, -x, -y)) {
						goto not_prime;
					}
				}
			}
		}

		cout << 'P' << endl;
		continue;

	not_prime:;
		cout << 'C' << endl;
	}

	return EXIT_SUCCESS;
}

