using namespace std;

const int MAX_RCS = 20;

int calc_indent(const string& s) {
	for(int i = 0; i < static_cast<int>(s.size()); ++i) {
		if(s[i] != '.') return i;
	}

	return s.size();
}

void calc_nest(int& nest_r, int& nest_c, int& nest_s, const string& line) {
	for(int i = 0; i < static_cast<int>(line.size()); ++i) {
		switch(line[i]) {
		case '(': ++nest_r; break;
		case ')': --nest_r; break;
		case '{': ++nest_c; break;
		case '}': --nest_c; break;
		case '[': ++nest_s; break;
		case ']': --nest_s; break;
		}
	}
}

bool ok(int r, int c, int s, const map<tuple<int, int, int>, int>& conditions) {
	for(const pair<tuple<int, int, int>, int>&e : conditions) {
		const tuple<int, int, int>& nest = e.first;
		const int indent = r * get<0>(nest) + c * get<1>(nest) + s * get<2>(nest);
		if(indent != e.second) return false;
	}

	return true;
}

inline int gcd(int a, int b, int c) {
	return __gcd(a, __gcd(b, c));
}

int get_value(int r, int c, int s, const map<tuple<int, int, int>, int>& conditions) {
	const int g = max(1, gcd(r, c, s));
	const tuple<int, int, int> t(r / g, c / g, s / g);
	if(conditions.count(t)) return g * conditions.at(t);
	return -1;
}

int main() {
	cin.tie(0);
	ios::sync_with_stdio(false);

	for(int p, q; cin >> p >> q && p;) {
		map<tuple<int, int, int>, int> conditions;
		int nest_r = 0, nest_c = 0, nest_s = 0;
		for(int i = 0; i < p; ++i) {
			string line;
			cin >> line;
			const int g = max(1, gcd(nest_r, nest_c, nest_s));
			conditions[make_tuple(nest_r / g, nest_c / g, nest_s / g)] = calc_indent(line) / g;
			calc_nest(nest_r, nest_c, nest_s, line);
		}

		unordered_set<int> R, C, S;
		for(int r = 1; r <= MAX_RCS; ++r) {
			for(int c = 1; c <= MAX_RCS; ++c) {
				for(int s = 1; s <= MAX_RCS; ++s) {
					if(ok(r, c, s, conditions)) {
						R.insert(r);
						C.insert(c);
						S.insert(s);
					}
				}
			}
		}

		nest_r = 0, nest_c = 0, nest_s = 0;
		vector<int> ans(q);
		for(int i = 0; i < q; ++i) {
			string line;
			cin >> line;
			const int indent = calc_indent(line);

			if(!((nest_r && R.size() != 1) ||
				 (nest_c && C.size() != 1) ||
				 (nest_s && S.size() != 1))) {
				ans[i] = abs(nest_r * (*R.begin()) + nest_c * (*C.begin()) + nest_s * (*S.begin()) - indent);
			}
			else if(ans[i] = get_value(nest_r, nest_c, nest_s, conditions), ans[i] != -1) {
				ans[i] = abs(ans[i] - indent);
			}
			else if(S.size() == 1 && (ans[i] = get_value(nest_r, nest_c, 0, conditions), ans[i] != -1)) {
				ans[i] = abs(ans[i] + (*S.begin()) - indent);
			}
			else if(C.size() == 1 && (ans[i] = get_value(nest_r, 0, nest_s, conditions), ans[i] != -1)) {
				ans[i] = abs(ans[i] + (*C.begin()) - indent);
			}
			else if(R.size() == 1 && (ans[i] = get_value(0, nest_c, nest_s, conditions), ans[i] != -1)) {
				ans[i] = abs(ans[i] + (*R.begin()) - indent);
			}
			calc_nest(nest_r, nest_c, nest_s, line);
		}

		for(int i = 0; i < q; ++i) {
			cout << ans[i] << (i == q - 1 ? "\n" : " ");
		}
	}

	return EXIT_SUCCESS;
}

