bool isPrime(int n){
	if(n<=1)return false;
	for(int i=2;i*i<=n;i++)
		if(n%i==0)return false;
	return true;
}

bool isGaussianPrime(int a, int b) {
  if (a < 0) a = -a;
  if (b < 0) b = -b;
  if (a == 0) return b % 4 == 3 && isPrime(b);
  if (b == 0) return a % 4 == 3 && isPrime(a);
  return isPrime(a*a+b*b);
}

