{

    auto check = [](int p, int q) {
        const auto squareNorm = p * p + q * q;
        for (int n = 0;; ++n) {
            if (n * n > squareNorm) {
                break;
            }
            for (int m = 0; m <= n; ++m) {
                if (m * m > squareNorm) {
                    break;
                }
                if (n * n + m * m <= 0) {
                    continue;
                }
                auto f = [p, q](int n_, int m_) {
                    // clang-format off
                    if ((n_ ==  1 && m_ ==  0) ||
                        (n_ ==  0 && m_ ==  1) ||
                        (n_ == -1 && m_ ==  0) ||
                        (n_ ==  0 && m_ == -1) ||
                        (n_ ==  p && m_ ==  q) ||
                        (n_ == -q && m_ ==  p) ||
                        (n_ == -p && m_ == -q) ||
                        (n_ ==  q && m_ == -p)) {
                        return false;
                    }
                    // clang-format on
                    return (n_ * p + m_ * q) % (n_ * n_ + m_ * m_) == 0 &&
                           (n_ * q - m_ * p) % (n_ * n_ + m_ * m_) == 0;
                };
                if (f(n, m) || f(n, -m) || f(-n, m) || f(-n, -m) || f(m, n) ||
                    f(m, -n) || f(-m, n) || f(-m, -n)) {
                    return false;
                }
            }
        }
        return true;
    };

    int N;
