bool issq(int x) { int y = (int)round(sqrt(x)); return y * y == x; }

pii mult(pii a, pii b) {
	return {a.first * b.first - a.second * b.second, a.first * b.second + a.second * b.first};
}

int multeq(int a, int b, int c, int d, int e, int f) {
	pii t = {e, f};
	if (mult({a, b}, {c, d}) == t || mult({a, -b}, {c, d}) == t || mult({a, b}, {c, -d}) == t || mult({a, -b}, {c, -d}) == t) return true;
	return false;
}

bool ok(int m, int n, int i, int j) {
	int iup = floor(sqrt(i)), jup = floor(sqrt(j));
	for (int ii = -iup; ii <= iup; ii++) if (issq(i - ii * ii)) {
		int a = ii, b = (int)round(sqrt(i - ii * ii)); // actually (a, -b) and (a, b)
		for (int jj = -jup; jj <= jup; jj++) if (issq(j - jj * jj)) {
			int c = jj, d = (int)round(sqrt(j - jj * jj));
			if (multeq(a, b, c, d, m, n))
				return true;
		}
	}
	return false;
}

