inline bool check(int p, int q, int m, int n) {
	return (m*p + n*q + m*m+n*n) % (m*m + n*n) == 0 && (m*q - n *p +m*m+n*n) % (m*m + n*n) == 0;
}

int main() {
	cin.tie(0); ios::sync_with_stdio(false);
	int N; cin >> N;
	for (int i = 0;i < N;i++) {
		int p, q; cin >> p >> q;
		int cnt = 0;
		int G = p * p + q * q;
		for (int m = 0; m*m <= G;m++) {
			for (int n = 0; m*m + n*n <= G;n++) {
				if (m*m + n*n == 0) continue;

				if (check(p, q, m, n)) cnt++;
				if (n != 0 && check(p, q, m, -n)) cnt++;
				if (m != 0 && check(p, q, -m, n)) cnt++;
				if (m != 0 && n != 0 && check(p, q, -m, -n))cnt++;
			}
		}

		if (cnt == 8) cout << "P" << endl;
		else cout << "C" << endl;

	}
}

