    complex<int> g(abs(m), abs(n));
    return (g.real() == 0 && g.imag() % 4 == 3 && p[g.imag()]) ||
           (g.imag() == 0 && g.real() % 4 == 3 && p[g.real()]) ||
           norm(g) == 2 || (norm(g) % 4 == 1 && p[norm(g)]);
}

