#include <iostream>

using namespace std;

int main()
{
    auto check = [](int p, int q) {
        const auto squareNorm = p * p + q * q;
        // enumerate all natural numbers
        for (int n = 0;; ++n) {
            // if <m, n> . <x, y> = <p, q> then (m^2 + n^2) * (x^2 + y^2) = p^2 + q^2
            // so, if n^2 > p^2 + q^2 then (x^2 + y^2) < 1. there is no such integers.
            if (n * n > squareNorm) {
                break;
            }
            for (int m = 0; m <= n; ++m) {
                if (m * m > squareNorm) {
                    break;
                }
                // m^2 + n^2 > 0
                if (n * n + m * m <= 0) {
                    continue;
                }
                auto f = [p, q](int n_, int m_) {
                    // trivial solution
                    if ((n_ ==  1 && m_ ==  0) ||
                        (n_ ==  0 && m_ ==  1) ||
                        (n_ == -1 && m_ ==  0) ||
                        (n_ ==  0 && m_ == -1) ||
                        (n_ ==  p && m_ ==  q) ||
                        (n_ == -q && m_ ==  p) ||
                        (n_ == -p && m_ == -q) ||
                        (n_ ==  q && m_ == -p)) {
                        return false;
                    }
                    // is divisor?
                    // note: this equation can derive by solving Ginkgo multiplication definition
                    return (n_ * p + m_ * q) % (n_ * n_ + m_ * m_) == 0 &&
                           (n_ * q - m_ * p) % (n_ * n_ + m_ * m_) == 0;
                };
                if (f(n, m) || f(n, -m) || f(-n, m) || f(-n, -m)) {
                    return false;
                }
            }
        }
        return true;
    };

    int N;
    cin >> N;
    for (int i = 0; i < N; ++i) {
        int n, m;
        cin >> n >> m;
        cout << (check(n, m) ? 'P' : 'C') << endl;
    }
}

