using namespace std;

typedef long long ll;

bool isEightDivisors(int p,int q,int m,int n){
  if( p ==  1 && q ==  0 ) return true;
  if( p ==  0 && q ==  1 ) return true;
  if( p == -1 && q ==  0 ) return true;
  if( p ==  0 && q == -1 ) return true;
  if( p ==  m && q ==  n ) return true;
  if( p == -n && q ==  m ) return true;
  if( p == -m && q == -n ) return true;
  if( p ==  n && q == -m ) return true;
  return false;
}

#define EPS (1e-8)

bool isSqrt(ll x){
  ll tmp = sqrt(x) + EPS;
  return tmp * tmp == x;
}

bool isPrime(int p,int q){
  int p2 = p * p, q2 = q * q;
  for(int a=1;a*a<=p2+q2;a++) {
    if( ( p2 + q2 ) % a ) continue;
    int b = ( p2 + q2 ) / a;

    int base_a = sqrt(a);
    int base_b = sqrt(b);

    for(int m=-base_a;m<=base_a;m++) {
      if( base_a * base_a > a ) continue;
      int n2 = a - m * m;

      if( !isSqrt(n2) ) continue;

      int n = sqrt(n2);

      if( ( m*m + n*n ) == 0 ) continue;

      if( !( ( m*p + n*q ) % ( m*m+n*n ) == 0 &&
             ( m*q - n*p ) % ( m*m+n*n ) == 0 ) ) continue;

      for(int x=-base_b;x<=base_b;x++){
        if( base_b * base_b > b ) continue;
        int y2 = b - x * x;
        //if( y2 <= 0 ) continue;
        if( !isSqrt(y2) ) continue;
        int y = sqrt(y2);
        //cout << m << "," << n << " " << x << "," << y << endl;
        if( !( ( m*x-n*y == p && m*y + n*x == q ) ||
               ( m*x-n*y == q && m*y + n*x == p ) ) ) continue;

        assert( ( m*x-n*y == p && m*y + n*x == q ) ||
                ( m*x-n*y == q && m*y + n*x == p ) );


        if( !isEightDivisors(m,n,p,q) && !isEightDivisors(m,n,q,p) ) {
          return false;
        }

        if( !isEightDivisors(x,y,p,q) && !isEightDivisors(x,y,q,p) ) {
          return false;
        }

      }
    }
  }
  return true;
}

int main(){
  int T;
  cin >> T;
  rep(_,T){
    int n,m;
    cin >> n >> m;
    puts(isPrime(n,m)?"P":"C");
  }
  return 0;
}

