using namespace std;

int m, n;
int isPrime(int x, int y){
	int sq = x * x + y * y;
	int d1 = x * m + y * n;
	int d2 = x * n - y * m;

	return ((d1 % sq == 0) && (d2 % sq == 0));
}

int main(){
	int dataset;
	cin >> dataset;
	while(dataset--){
		cin >> m >> n;
		int cnt = 0;
		for(int x = 0; x * x <= m * m + n * n; x++){
			for(int y = 0; x * x + y * y <= m * m + n * n; y++){
				if(x == 0 && y == 0) continue;
				cnt += isPrime(x, y);
				if(x != 0) cnt += isPrime(-x, y);
				if(y != 0) cnt += isPrime(x, -y);
				if(x != 0 && y != 0) cnt += isPrime(-x, -y);

			}
		}
		if(cnt > 8){
			cout << "C" << endl;
		}else{
			cout << "P" << endl;
		}
	}
}

