bool D(int p,int q,int m,int n)
{
  if((m*p+n*q)%(m*m+n*n)==0 && (m*p-n*q)%(m*m+n*n)==0)return true;
  return false;
}

bool judge(int p,int q)
{
  int c=0;
  for(int i=1;i*i<=p*p+q*q;i++){
    for(int j=i;j*j<=p*p+q*q;j++){
      if(i!=p || j!=q){
      if(D(p,q,i,j))c++;
      if(D(p,q,i,(-1)*j))c++;
      if(D(p,q,(-1)*i,j))c++;
      if(D(p,q,(-1)*i,(-1)*j))c++;
      }
    }
  }
  /*cout<<endl;
    cout<<c<<endl;*/
  if(c==0)return true;
  return false;
}

int main()
{
  int n,m,N;
  cin>>N;
  for(int i=0;i<N;i++){
    cin>>m>>n;
    if(judge(m,n))cout<<"P"<<endl;
    else cout<<"C"<<endl;
  }
  return 0;
}

