    for(int i = 0 ;i < cas;i++) {
        scanf("%d%d",&m,&n);
        if(m *m + n * n <= 1) {
            puts("C");
            continue;
        }
        int div = 0;
        int sq = sqrt((double) m*m + n*n) + 0.1;
        for(int f = -sq; f <= sq;f ++)
            for(int g = -sq;g <= sq;g++) {
                int tt = f*f + g*g;
                if(0 == tt)
                    continue;
                int a = m*f + n*g;
                int b = m*g - n*f;
                if(0 == a%tt && 0 == b%tt) {
                    div ++;
                }
            }
        if(8 == div)
            puts("P");
        else
            puts("C");
    }
    return 0;
}

#include<iostream>
