vector< pair<int,int> > exist[20010];
int chk(int p,int q){
	int ans = 0;
	for(int i = -200 ; i <= 200 ; i++){
		for(int j = -200 ; j <= 200 ; j++){
			if( i*i+j*j < 1 ) continue;
			if( (p*p+q*q) % (i*i+j*j) == 0 ){
				for(int k = 0 ; k < exist[(p*p+q*q) / (i*i+j*j)].size() ; k++){
					int x =  exist[(p*p+q*q) / (i*i+j*j)][k].first;
					int y =  exist[(p*p+q*q) / (i*i+j*j)][k].second;
					if( i*x-y*j == p && i*y+j*x == q ) ans++;
				}
			}
		}
	}
	return ans == 8;
}
