struct GinkoNumber
{
	int m;
	int n;

	GinkoNumber(int m, int n) : m(m), n(n){}

	GinkoNumber operator *(const GinkoNumber& data) const {
		return GinkoNumber(m*data.m - n * data.n, m*data.n + n * data.m);
	}

	GinkoNumber operator +(const GinkoNumber& data) const {
		return GinkoNumber(m*data.m + n * data.n, m*data.n - n * data.m);
	}
};

