#include <bits/stdc++.h>
using namespace std;

string solve(int m, int n)
{
	vector<int> f(20000, 0);
	m = abs(m);
	n = abs(n);
	if (m > n) swap(m, n);

	int t = m * m + n * n;

	for (int i = 1; i * i <= t; i++){
		for (int j = 1; i * i + j * j <= t; j++){
			f[i * i + j * j] = 1;
		}
	}

	for (int i = 0; i <= t; i++){
		for (int j = i; i * i + j * j <= t; j++){
			int s = i * i + j * j;
			if (s == 0) continue;
			if (t % s) continue;
			if (i == 0 && j == 1) continue;
			if (i == m && j == n) continue;
			if (f[t / s]) return "C";
		}
	}

	return "P";
}

int main()
{
	int q;
	cin >> q;
	for (int i = 0; i < q; i++){
		int m, n;
		cin >> m >> n;
		cout << solve(m, n) << endl;
	}
}

