bool D(int p,int q,int m,int n)
{
  if((m*p+n*q)%(m*m+n*n)==0 && (m*q-n*p)%(m*m+n*n)==0)return true;
  return false;
}

bool judge(int p,int q)
{
  int c=0;
  for(int i=0;i*i<=p*p+q*q;i++){
    for(int j=0;j*j<=p*p+q*q;j++){
      if(i+j==0)continue;
      if(D(p,q,i,j))c++;
      if(j!=0 && D(p,q,i,-j))c++;
      if(i!=0 && D(p,q,-i,j))c++;
      if(i!=0 && j!=0 && D(p,q,-i,-j))c++;
    }
  }
  if(c==8)return true;
  return false;
}

