int n;

bool not_trivial(int a, int b, int m, int n)
{
	if (a ==  1 && b ==  0) return false;
	if (a ==  0 && b ==  1) return false;
	if (a == -1 && b ==  0) return false;
	if (a ==  0 && b == -1) return false;
	if (a ==  m && b ==  n) return false;
	if (a == -n && b ==  m) return false;
	if (a == -m && b == -n) return false;
	if (a ==  n && b == -m) return false;
	return true;
}

bool can_devide(int p, int q, int m, int n)
{
	int t = m*m + n*n;
	if (t == 0) return false;
	int x = m*p+n*q;
	int y = -n*p+m*q;
	return x%t==0&&y%t==0&&not_trivial(x/t, y/t, p, q);
}

int main()
{
	cin >> n;
	rep(i, n) {
		int p, q;
		cin >> p >> q;
		bool f = false;
		for (int m = 0; m*m < 100000; m++) {
			for (int n = 0; m*m+n*n < 100000; n++) {
				if (can_devide(p, q, m, n)
					|| can_devide(p, q, m, -n)
					|| can_devide(p, q, -m, n)
					|| can_devide(p, q, -m, -n)
					) {
					f = true;
					goto END;
				}
			}
		}
END:
		printf("%c\n", f?'C':'P');
	}
	return 0;
}

