struct Edge{
	int  from,to,d,c;
	Edge(int u,int v,int d,int c):from(u),to(v),d(d),c(c){};
};
struct Task{
    int prev,pos,d;
    Task(int prev,int pos,int d)
        :prev(prev),pos(pos),d(d){};
    bool operator>(const Task& r) const{
        return d>r.d;
    }
};
typedef vector<vector<Edge> > Graph;

vector<int> dijkstra(const Graph& g,const int s,vector<int>& prev){
        const int V=g.size();
        vector<int> d(V,INF);d[s]=0;
        fill(ALL(prev), -2);
        priority_queue<Task,vector<Task>,greater<Task> > que;
        que.push(Task(-1,s,0));
        while(!que.empty()){
            Task task=que.top();que.pop();
            //if(prev[task.pos]!=-2)continue;
            if(d[task.pos]<task.d)continue;
            prev[task.pos]=task.prev;
            EACH(e,g[task.pos]){
                if(d[e->to]>d[e->from]+e->d){
                    d[e->to]=d[e->from]+e->d;que.push(Task(e->from,e->to,d[e->to]));
                }
            }   
        }
        return d;
    }
    vector<int> dijkstra(const Graph& g,const int s){
        vector<int> prev(g.size());return dijkstra(g,s,prev);
    }

