      bool is_ok = true;
      for(int x=0;x*x<20000;x++){
	for(int y=0;x*x+y*y<20000;y++){
	  if(x*x+y*y <= 1) continue;

	  for(int mi=0;mi<4;mi++){
	    if(is_divisor(P(x*mx[mi],y*my[mi]),P(m,n)) 
	       && !is_eight_divisor(P(x*mx[mi],y*my[mi]),m,n)){
	      is_ok = false;
	      goto found;
	    }
	  }
	}
      }
    found:;
      printf("%c\n",is_ok ? 'P' : 'C');
    }
  }
}

