            if(t%i == 0){
                auto mp = prime_factor(t/i);

                bool f = true;
                for(auto e: mp){
                    if(e.first % 4 != 3) continue;
                    if(e.second % 2 == 0) continue;
                    f = false;
                }
                if(f){
                    cnt++;
                }
            }
        }
        if(cnt >= 3) cout << "C" << endl;
        else cout << "P" << endl;

    }
    return 0;
}

