using namespace std;

bool isDivisor(int m, int n, int x, int y) {
    if (x == 0 && y == 0) return false;
    int mod = x * x + y * y;
    int p = x * m + y * n;
    int q = x * n - y * m;
    //cout << m << ',' << n << ',' << x << ',' << y << endl;
    return p % mod == 0 && q % mod == 0;
}

bool isPrime(int m, int n) {
    int c = 0;
    for (int x = -200; x <= 200; x++) {
        for (int y = -200; y <= 200; y++) {
            if (isDivisor(m, n, x, y)) {
                c++;
            }
        }
    }
    return c == 8;
}

int main() {
    int n; cin >> n;
    while (n--) {
        int m, n; cin >> m >> n;
        cout << (isPrime(m, n) ? 'P' : 'C') << endl;
    }
    return 0;
}

