	static int[] sq = new int[20001];

	public void run() {
		for (int i = 1; i * i <= 20000; i++) 
			sq[i * i] = i;

		int T = in.nextInt();
		outer : 
		for (int times = 0; times < T; times++) {
			int m = in.nextInt(), n = in.nextInt();
			int A = m * m + n * n;
			solve(m, n);
			for (int i = 2; i * i <= A; i++) {
				if (A % i != 0) continue;
				for (int p = 1; p * p <= i; p++) {
					int q = sq[i - p * p];
					if (p * p != i && q == 0) continue;

					if ((m * p + n * q) % i == 0 && (m * q - n * p) % i == 0) {
						System.out.println("C");
						continue outer;
					}
				}
			}
			System.out.println("P");
		}
		out.close();
	}

	static boolean solve(int M, int N) {
        int D = M * M + N * N;
        for (int i = 2; i * i <= D; ++i) {
            if (D % i != 0) continue;
            for (int p = 1; p * p <= i; ++p) {
                int q = sq[i - p * p];
                if (p * p != i && q == 0) continue;
                if ((M * p + N * q) % i == 0 && (M * q - N * p) % i == 0) {
                	System.out.println(p + " " + q);
                	return false;
                }
            }
        }
        return true;
    }

