class Union_Find_Tree{
	public:
	vi p,r,s;
	Union_Find_Tree(int n){
		p=r=vi(n);
		s=vi(n,1);
		for(int i=0;i<n;i++) p[i]=i;
	}
	int Par(int x){
		if(p[x]==x) return x;
		return p[x]=Par(p[x]);
	}
	int Size(int x){return s[Par(x)];}
	bool Unite(int x,int y){
		x=Par(x);
		y=Par(y);
		if(x==y) return 0;
		if(r[x]<r[y]){
			p[x]=y;
			s[y]+=s[x];
		}
		else{
			p[y]=x;
			s[x]+=s[y];
			if(r[x]==r[y]) r[x]++;
		}
		return 1;
	}
	bool Same(int x,int y){return Par(x)==Par(y);}
};

int main(){
	ios::sync_with_stdio(0);
	cin.tie(0);
	int n,m;
	cin>>n>>m;
	vp a(m);
	for(int i=0;i<m;i++){
		cin>>a[i].first>>a[i].second;
	}
	while(1){
		bool B=1;
		for(int i=0;i<m&&B;i++) for(int j=i+1;j<m&&B;j++) {
			int l=a[i].first,r=a[i].second,L=a[j].first,R=a[j].second;
			if(max(l,L)<min(r,R)){
				a.erase(a.begin()+j);
				a.erase(a.begin()+i);
				a.push_back({min(l,L),max(r,R)});
				m--;
				B=0;
			}
		}
		if(B) break;
	}
	int res=n+1;
	for(int i=0;i<m;i++){
		res+=2*(a[i].second-a[i].first);
	}
	cout<<res<<endl;
}

