void solve(void){

	int N, m;
	cin >> N >> m;

	vec imos(N+1, 0);

	while(m--)
	{
		int c, d;
		cin >> c >> d;
		imos[--c]++;
		imos[--d]--;
	}

	rep(N, i)
	{
		imos[i+1] += imos[i];
	}

	int ans = N+1, pre = 0, f = 0;

	rep(N, i)
	{
		if (imos[i] && !f)
		{
			pre = i;
			f = 1;
		}
		if (!imos[i] && f)
		{
			ans += (i-pre)*2;
			f = 0;
		}
	}

	if (f) ans += (N-pre) * 2;

	cout << ans << endl;

}

