// 4->1, 7->3, 9->8, 5->2
// (9, 8)
// (7, 3)
// (5, 2)
// (4, 1)

int main()
{
	int N, m;
	cin>>N>>m;
	vector<P> list(m), tmp;

//	list[i].first -> list[i].second
	rep(i, m)
		cin>>list[i].second>>list[i].first;

	sort(ALL(list), greater<P>());
	list.push_back(P(-1, -1));

	int a = list[0].first, b = list[0].second;
	for (int i=1; i<m+1; i++) {
		if (b <= list[i].first) {
			b = list[i].second;
		} else {
			tmp.push_back(P(a, b));
			a = list[i].first, b = list[i].second;
		}
	}
	tmp.push_back(P(0, 0));
	tmp.push_back(P(N+1, N+1));
	sort(ALL(tmp));

	int ans=0;
	for (int i=1; i<tmp.size(); i++){
		ans += tmp[i].first - tmp[i-1].second;
		ans += tmp[i].first - tmp[i].second;
	}
	cout << ans << endl;
}

