int main() {

    int N, M;
    cin >> N >> M;

    vector<pair<int, int>> edges(M);
    for (int i = 0; i < M; i++) {
        int c, d;
        cin >> c >> d;
        edges[i] = make_pair(c, d);
    }
    sort(edges.begin(), edges.end());

    vector<pair<int, int>> newEdges;
    pair<int, int> edge(-1, -1);
    for (int i = 0; i < M; i++) {
        if (edge == make_pair(-1, -1)) {
            edge = edges[i];
            continue;
        }
        if (edge.second >= edges[i].first) {
            edge.second = max(edge.second, edges[i].second);
        } else {
            newEdges.push_back(edge);
            edge = edges[i];
        }
    }
    if (edge != make_pair(-1, -1)) newEdges.push_back(edge);

    int cost = 0;
    cost += N + 1;
    for (int i = 0; i < newEdges.size(); i++) {
        cost += (newEdges[i].second - newEdges[i].first) * 2;
    }

    cout << cost << endl;

    return 0;
}

