#define frs first
#define scn second
#define sz(x) int((x).size())
#define all(x) (x).begin(), (x).end()
using namespace std;

typedef pair< int, int > Pr;

int N, M;
vector< Pr > C;

void read();
void solve();

bool cmpf(const Pr &, const Pr &);

int main() {
  read();
  solve();
  return 0;
}

void read() {
  int c, d;

  cin >> N >> M;
  for (int i = 0; i < M; ++i) {
    cin >> c >> d;
    C.phb(mkp(c, d));
  }
}

void solve() {
  vector< Pr > cp;

  for (int i = 0; i < M; ++i) {
    bool ign = false;

    for (int j = 0; j < M && !ign; ++j)
      if (i != j && ((C[i].frs >= C[j].frs) && (C[i].scn <= C[j].scn)))
        ign = true;

    if (!ign)
      cp.phb(C[i]);
  }

  stable_sort(all(cp), cmpf);

  int res = N + 1;

  for (int i = 0, j = 0, ed; i < sz(cp); i = j) {
    j = i + 1, ed = cp[i].scn;

    while (j < sz(cp) && (ed >= cp[j].frs))
      ed = cp[j].scn, ++j;

    res += 2 * (ed - cp[i].frs);
  }

  cout << res << "\n";
}

bool cmpf(const Pr &ra, const Pr &rb) {
  return ra.frs < rb.frs;
}

