			f[i]=min(f[i],f[j]+(s[i][1]-s[j][1])+2*(s[i][1]-minc));
			minc=min(minc,s[j][0]);
		}
		f[i]=min(f[i],s[i][1]+2*(s[i][1]-minc));
	}
	printf("%d\n",f[m-1]+(n+1-s[m-1][1]));
}

