bool intersect(ioi a,ioi b){
	if (a.l>b.l) swap(a,b);
	if (b.l<=a.r) return true;
	else return false;
}

ioi combine(ioi a,ioi b){
	a.l = min(a.l, b.l);
	a.r = max(a.r, b.r);
	return a;
}

