	});
	ioi current_segment = seg[1];
	for (int i=1;i<=m;i++){
		if (intersect(seg[i], current_segment)){
			current_segment = combine(current_segment, seg[i]);
		}
		else{
			res += 2*(current_segment.r - current_segment.l);
			current_segment = seg[i];
		}
	}

