  for(int i = 0; i < m; i++) {
    int c, d;
    cin >> c >> d;
    cds.push_back(make_pair(c,d)); // visits c after d (> c)
  }
  sort(cds.begin(), cds.end());
  // overlap -> rewind at onece
  int len = n + 1;
  int c = -1, d = -1;
  for(int i = 0; i < m; i++) {
    if(d < cds[i].first) { // do the previous overlap
      len += 2 * (d - c);
      c = cds[i].first;
      d = cds[i].second;
    } else { // overlapping
      d = max(d, cds[i].second);
    }
  }
  len += 2 * (d - c);
  cout << len << endl;
  return 0;
}

