typedef pair<int,int> P;

P t[1002];

int main() {
  int n, m;

  while (cin >> n >> m) {
    for (int i = 0; i < m; i++) {
      cin >> t[i].first >> t[i].second;
    }
    sort(t, t + m);

    vector<P> v;

    for (int i = 0; i < m; i++) {
      int lft = t[i].first;
      int rht = t[i].second;
      int j = i + 1;

      while (j < m && lft <= t[j].first && t[j].first <= rht) {
        rht = max(rht, t[j].second);
        j++;
      }

      v.push_back(P(lft, rht));
      i = j - 1;
    }

    int ans = 0;
    int now = 0;

    for (int i = 0; i < v.size(); i++) {
      ans += abs(now - v[i].second);
      ans += v[i].second - v[i].first;
      now = v[i].first;
    }

    ans += (n + 1) - now;

    cout << ans << endl;
  }
}

