    for(int i = 0 ; i < c ; i++){
        cin >> a >> b;
        if(lock[a] < b){
            lock[a] = b;
        }
    }

    //cout << cur << endl;

    while(cur < n+1){
        //cout << cur << endl;
        if(lock[cur] != 0 && lock[cur] > maxx) maxx = lock[cur];
        if(lock[cur] != 0 && minn == -1) minn = cur;

