#define REP(i, n) for (int i = 0; (i) < int(n); ++ (i))
using namespace std;

int main() {
    // input
    int n, m; scanf("%d%d", &n, &m);
    vector<int> c(m), d(m);
    REP (i, m) scanf("%d%d", &c[i], &d[i]);
    // solve
    vector<int> imos(n + 2);
    REP (i, m) {
        imos[c[i]] += 1;
        imos[d[i]] -= 1;
    }
    int result = n + 1;
    REP (i, n + 1) {
        imos[i + 1] += imos[i];
        result += 2 * bool(imos[i + 1]);
    }
    // output
    printf("%d\n", result);
    return 0;
}

#include <cstdio>
#include <utility>
