typedef pair<int,int> P;

int main(){
  int N,m,c,d,ans=0;
  vector<int> v2;
  vector<P> v;
  cin>>N>>m;
  for(int i=0;i<m;i++) cin>>c>>d,v.push(make_pair(c,d));
  sort(v.begin(),v.end());
  if(v.sz!=0)
    for(int i=0;i<v.sz-1;i++)
      if(v[i+1].f<=v[i].s){
	v[i].s=max(v[i].s,v[i+1].s);
	v.erase(v.begin()+i+1);
