  int V[N]={0};
  cin >> n >> m;
  for(int i=0;i<m;i++){
    cin >> c >> d;
    V[c]=max(V[c],d);
  }
  l=r=-1;
  flag=sum=0;
  for(int i=0;i<=n;i++){
    if(r==i) sum+=3*(r-l)+1,flag=0;
    else if(flag&&V[i]!=0) r=max(r,V[i]);
