	int N, m; cin >> N >> m;
	VI dependencies(N+1, 0);
	REP (i, m) {
		int c, d; cin >> c >> d;
		dependencies[c] = max(dependencies[c], d);
	}
	dump(dependencies);

	int ans = 0;
	int back = INF;
	map<int, int> backto;
	FOR (i, 1, N+1) {
		ans++;
		dump(i);
		if (EXIST(backto, i)) {
			dump(back);
			backto.erase(i);
			if (backto.size() == 0) {
				dump(i - back);
				ans += (i - back) * 2;
				back = INF;
			}
		}
		if (dependencies[i] != 0) {
			if (dependencies[i] > i) {
				if (EXIST(backto, dependencies[i])) {
					backto[dependencies[i]] = min(backto[dependencies[i]], i);
				} else {
					backto[dependencies[i]] = i;
				}
				back = min(back, i);
			}
		}
		dumpl(backto);
	}
	cout << ans + 1 << endl;

	return 0;
}

