  }
  if ( dp[pre][now][n] > 0 ) return dp[pre][now][n];

  int ret = dfs(min(pre, d[n].sc), d[n].fr, n+1) + (d[n].fr-now);
  ret = min(ret, dfs(d[n].sc, d[n].fr, n+1) + (now-pre + d[n].fr-pre));

  return dp[pre][now][n] = ret;
}

