	map< int, vector<int> > memo;
	rep(i, 0, M)
	{
		int c, d;
		cin >> c >> d;
		memo[c].push_back(i);
		memo[d].push_back(i);
	}

	ll ans = 0;
	set<int> first, second;
	int memo_index = -1;
	rep(i, 1, N + 1)
	{
		ans++;

		if (memo[i].size() == 0) continue;

		if (memo_index < 0) memo_index = i;

		for (int n : memo[i])
		{
			if (first.find(n) == first.end())
				first.insert(n);
			else
				second.insert(n);
		}

		if (first.size() == second.size())
		{
			ans += (i - memo_index) * 2;
			memo_index = -1;
			first.clear();
			second.clear();
		}
	}
	cout << ans + 1 << endl;
}

