vector<pair<int,int> > merge_intervals(vector<pair<int,int> > const& ps) {
    if (ps.empty()) return {};
    auto qs = ps;
    sort(qs.begin(), qs.end());
    vector<pair<int,int> > ret = { qs[0] };
    rep (i,qs.size()-1) {
        auto &p = ret.back();
        auto const& q = qs[i+1];
        if (p.second < q.first) ret.emplace_back(q);
        else chmax(p.second, q.second);
    }
    return ret;
}

