    for (int i = 0; i < rec.size(); ++i) {
        long long xl = rec[i].first, xr = rec[i].second;
        xma[xl]++; xma[xr]++;
    }
    int nx = 0;
    for(map<long long,int>::iterator it = xma.begin(); it != xma.end(); ++it) {
        it->second = nx++; xpl.push_back(it->first);
